INSERT INTO `core_expert` VALUES (29, '14383558720', '黄荣彬', NULL, '厦门大学', NULL, '黄荣彬（1960.7—）1977年进入厦门大学化学系学习，四年后又在厦门大学化学系攻读无机化学硕士学位。1985年研究生毕业后留校任教。现为\n厦门大学化学系教授、博士生导师。多年来工作在教学和科研第一线，参加和主持完成了多项国家科学基金科研项目，主要研究方向：1、配位化学，配合物合成：卟啉类大环配体络合物的合成及其做为光电转化材料，单加氯酶模型物的研究。', 'images/default_user_icon.jpg', 'http://www.kejso.com/scholar/14383558720', NULL, '2022-05-08 00:55:03.437753', NULL, NULL, '教授');
