INSERT INTO `core_expert` VALUES (375, '38172831944', '曹春香', NULL, '中国科学院遥感与数字地球研究所', NULL, '简历：\n.TRS_EditorP{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorDIV{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorTD{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorTH{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorSPAN{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorFONT{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorUL{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorLI{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorA{\n	LINE-HEIGHT:1.8;MARGIN-TOP:0px;FONT-FAMILY:宋体;MARGIN-BOTTOM:12px;FONT-SIZE:10.5pt\n}\n.TRS_EditorP{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorDIV{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorTD{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorTH{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorSPAN{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorFONT{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorUL{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorLI{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}.TRS_EditorA{margin-top:0px;margin-bottom:12px;line-height:1.8;font-family:宋体;font-size:10.5pt;}曹春香,中国科学院遥感与数字地球研究所研究员，中国科学院大学教授，博士生导师。现担任遥感科学国家重点实验室环境健康遥感研究室主任、中国科学院遥感与数字地球研究所公共卫生领域空间信息技术应用研究中心执行主任，兼任863地球观测与导航技术领域重大项目总体专家组专家、国家湿地科学技术第二届专家委员会委员、国家沙漠公园评审委员会委员、中国湿地保护协会理事、中国卫生信息学会卫生地理信息系统专委会委员等；被InternationalJournalofRemoteSensing、Geomatics，NaturalHazardsandRisk、Positioning、BMCInfectiousDiseases、FrontiersofEarthScience等权威期刊邀请为编委或特邀审稿。回国后创立了“环境健康遥感诊断”交叉学科方向；开创了“环境健康遥感诊断博士生课程”；从2011年开始每年主办一次“环境健康遥感诊断国际学术研讨会”；发表期刊和学会论文100余篇，其中SCI检索论文50余篇（平均影响因子1.832）；先后承担和参与国家重大专项、973、863、自然基金及民用航天预研项目等项目20项；获得软件著作权7项；申请发明专利6项。通过横跨遥感领域的所有国家级科研攻关及人才培养和相关的学术与社会活动，为中国遥感事业的发展作出了相应贡献。曹春香研究员本着“守时、自信、协作、创新、善始善终、JustInTime”的理念，引进与培养了不同层次的各类优秀人才。通过环境健康遥感诊断的交叉学科方向吸引了本领域国内外知名学者和专家及优秀青年科技工作者来室协同研究，与美国著名教授合作为本所在“自然”期刊上实现了零的突破；在本土已培养遥感专业研究生20余名，并推动获得学位的研究生均进入中国科学院系统、国家航天部门、国家林业系统等建功立业，其中已有4名晋升为高级职称，成为了部门研究骨干。培养了本所第一位授予博士学位的泰国公主奖学金的留学生；招收了来自泰国、尼泊尔、巴基斯坦、伊朗、蒙古、斯里兰卡的留学生和实习生12名；选派年轻职工和学生到美国波士顿大学、德国马普学会、日本京都大学、福井大学、北海道大学等国际知名高校和学术机构学习。面向社会，为国家林业局及国家与地方疾病预防控制相关部门等培训了一批批环境健康遥感诊断专业人才；面向社会公益活动也作为女科学家的代表在中国妇联、中国女科学家协会组织的讲堂里向民众及中学生等科普环境健康遥感诊断知识。曹春香引领已具备国际竞争的环境健康遥感诊断团队，将遥感科学攻关与领域尖端人才培养搭载于环境健康遥感诊断的平台，通过在全国创建的北京、上海、深圳、成都、河北、内蒙古、江西及福建公共卫生领域空间技术应用研究中心，正在将', 'images/default_user_icon.jpg', 'http://www.kejso.com/scholar/38172831944', NULL, '2022-05-08 00:57:12.254471', NULL, NULL, NULL);
